# ######### CMR AND EGI API FORMATTING TESTS ######################################################


def test_bounding_box_fmt():
    bbox = spat.Spatial([-55, 68, -48, 71])
    obs = bbox.fmt_for_CMR()
    exp = "-55.0,68.0,-48.0,71.0"
    assert obs == exp


def test_bbox_fmt():
    bbox = spat.Spatial([-55, 68, -48, 71])
    obs = bbox.fmt_for_EGI()
    exp = "-55.0,68.0,-48.0,71.0"
    assert obs == exp


